-- ----------------------------
-- Procedure structure for `AdStateChange`
-- ----------------------------
DROP PROCEDURE IF EXISTS `AdStateChange`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `AdStateChange`()
BEGIN
	#Routine body goes here...
	UPDATE adinadp SET state=0 WHERE advertisementId in (SELECT advertisementId from advertisement WHERE startTime < NOW());
	UPDATE adinadp SET state=1 WHERE advertisementId in (SELECT advertisementId from advertisement WHERE endTime < NOW());
END
;;
DELIMITER ;

-- ----------------------------
-- Event structure for `AdStateEvent`
-- ----------------------------
DROP EVENT IF EXISTS `AdStateEvent`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` EVENT `AdStateEvent` ON SCHEDULE EVERY 1 DAY STARTS '2014-07-07 17:23:29' ON COMPLETION PRESERVE DISABLE DO CALL AdStateChange()
;;
DELIMITER ;
